function Context() {
    this.htdocsDir = './htdocs';
    return this;
}

Context.prototype.handle = function(req, res) {
    var target = this.dispatch(req, res);
    this.render(target, req, res);
}

Context.prototype.dispatch = function(req, res) {
    return './htdocs/_folder';
}

Context.prototype.render = function(target, req, res) {
    res.writeHead(200, {'Content-Type': 'text/plain'});
    res.end('Hello World\n');
}

module.exports = Context;
